mapper.select:
SELECT movie_id, movie_title, release, nation, genre, runningtime, grade, outline, director, actor, ott, movie_point, file_id 
FROM movie
WHERE movie_id = #{movieId}

mapper.update:
UPDATE mypage
SET mypage_point = #{mypagePoint}, mypage_comment = #{mypageComment}
WHERE movie_id = #{movieId} AND user_id = #{userId}
